import React, { Component } from "react";
import { InfoConsumer } from "../context";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import ScienceInfo from "../ScienceInfo";
import FassInfo from "../FassInfo";
import Stalls from "./Stalls";
import Info from "./Info";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import Location from "./Location";

class Canteens extends Component {
    constructor(props, context) {
        super(props, context);

        this.state = {
            scienceShow: false,
            fassShow: false,
            canteens: [
                {
                    headerTitle: "Frontier",
                    headerSubTitle: "soluta numquam",
                    headerText: "Lorem ipsum dolor, sit amet consectetur adipisicing elit. Odit laboriosam cum recusandae quas? Natus, a    lias architecto error, sunt quae non placeat a eius minus maiores libero laborum perferendis, nobis qui?",
                    title: "Molestias officia excepturi2",
                    faculty: "Science",
                    description: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Voluptates qui voluptate odio quod doloremque     unde reprehenderit velit quaerat. Deserunt voluptatem natus suscipit aliquid, eligendi sequi dicta porro? Laudantium    , libero est?",
                    img: ".././images/canteens/frontier.jpg",
                    maps: "https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d1123.3850145412064!2d37.600589158335865!3d55.7277    4864513666!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x46b54b0be3ac9d11%3A0x735806d25b3bdc55!2z0JzRg9C30LXQu    SDRgdC-0LLRgNC10LzQtdC90L3QvtCz0L4g0LjRgdC60YPRgdGB0YLQstCwIMKr0JPQsNGA0LDQtsK7!5e0!3m2!1sru!2sru!4v1547763408711",
                    show: false
                }
            ]
        };
    }

    /*
    onClose(fac) {
        this.setState({ fac+`Show`: false });
    }
    */

    scienceClose () {
        this.setState({ scienceShow: false });
    }

    fassClose () {
        this.setState({ fassShow: false });
    }

    render() {
        return (
            <Router>
                <InfoConsumer>
                    <div className="container">
                        {/*
                        <Location 
                            faculty="science"
                            onClose={this.scienceClose.bind(this)}
                            fassClose={this.fassClose.bind(this)}
                            location="https://www.google.com/maps/embed?pb=!1m18!1m12!    1m3!    1d5691.965642731867!2d103.77773491402935!3d1.2958058252296165!2m3!1f0!2f0!3f0!3m2        !1i1024!2i768!4f13.1!3m3!1m2!1s0x31da1a50d2a84f95%3A0x10368845ce4e2bb0!2sNUS+Science+        Canteen+(Frontier)!5e0!3m2!1sen!2ssg!4v1561537467347!5m2!1sen!2ssg"
                            show={this.state.scienceShow}
                        />
                        */}

                        <InfoConsumer>
                            {value => {
                                return value.canteens.map(item => {
                                    return (
                                        <Info 
                                            item={item} 
                                            onClick={this.handleOnClick.bind(this)}
                                        />
                                    );
                                });
                            }}
                        </InfoConsumer>
                        <h4 className="mt-5">
                            Science Canteen
                            <Button
                                className="ml-2"
                                variant="info"
                                onClick={() => this.setState({ scienceShow: true })}
                            >
                                Location
                            </Button>
                        </h4>
                        <div className="row mt-3">
                            <InfoConsumer>
                                {value => {
                                    return value.canteens.map(item => {
                                        return (
                                            <ScienceInfo key={item.id} item={item} />
                                        );
                                    });
                                }}
                            </InfoConsumer>
                        </div>

                        <h4 className="mt-5">
                            Fass Canteen
                            <Button
                                className="ml-2"
                                variant="info"
                                onClick={() => this.setState({ fassShow: true })}
                            >
                                Location
                            </Button>
                        </h4>
                        <div className="row mt-3">
                            {value => {
                                return value.canteens.map(item2 => {
                                    return (
                                        <FassInfo key={item2.id} item2={item2} />
                                    );
                                });
                            }}
                        </div>
                    </div>
                </InfoConsumer>

                <Route path={`canteen/:stall`} component={Stalls} />
            </Router>
        );
    }
}

export default Canteens;
