import React, { Component } from 'react';
import axios from 'axios';

export default class CreateFood extends Component {

    constructor(props) {
        super(props);

        this.onChangeFoodName = this.onChangeFoodName.bind(this);
        this.onChangeFoodDescription = this.onChangeFoodDescription.bind(this);
        this.onChangeFoodPrice = this.onChangeFoodPrice.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            food_name: '',
            food_description: '',
            food_price: '',
        }
    }

    onChangeFoodName(e) {
        this.setState({
            food_name: e.target.value
        });
    }

    onChangeFoodDescription(e) {
        this.setState({
            food_description: e.target.value
        });
    }

    onChangeFoodPrice(e) {
        this.setState({
            food_price: e.target.value
        });
    }

    onSubmit(e) {
        e.preventDefault();

        console.log(`Form submitted:`);
        console.log(`Food Name: ${this.state.food_name}`);
        console.log(`Food Description: ${this.state.food_description}`);
        console.log(`Food Price: ${this.state.food_price}`);

        const newFood = {
            food_name: this.state.food_name,
            food_description: this.state.food_description,
            food_price: this.state.price
        };

        axios.post('http://localhost:4000/foods/add', newFood)
            .then(res => console.log(res.data));


        this.setState({
            food_name: '',
            food_description: '',
            food_price: ''
        })
    }

    render() {
        return (
            <div style={{marginTop: 10}}>
                <h3>Create New Food</h3>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>Name: </label>
                        <input type="text"
                               className="form-control"
                               value={this.state.food_name}
                               onChange={this.onChangeFoodName}
                               />
                    </div>
                    <div className="form-group">
                        <label>Description: </label>
                        <input type="text"
                               className="form-control"
                               value={this.state.food_description}
                               onChange={this.onChangeFoodDescription}
                               />
                    </div>
                    <div className="form-group">
                        <label>Price: </label>
                        <input type="text"
                               className="form-control"
                               value={this.state.food_price}
                               onChange={this.onChangeFoodPrice}
                               />
                    </div>

                    <div className="form-group">
                        <input type="submit" value="Create Food" className="btn btn-primary" />
                    </div>
                </form>
            </div>
        )
    }
}
