import React, { Component } from 'react';
import axios from 'axios';

export default class CreateFood extends Component {

    constructor(props) {
        super(props);

        //this.onChangeFoodName = this.onChangeFoodName.bind(this);
        //this.onChangeFoodDescription = this.onChangeFoodDescription.bind(this);
        //this.onChangeFoodPrice = this.onChangeFoodPrice.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            type: 'canteen',
            food_name: '',
            food_description: '',
            food_price: '',
        }
    }

    handleTypeChange(e) {
        this.setState({type: e.target.value});
    }

    /*
    onChangeFoodName(e) {
        this.setState({
            food_name: e.target.value
        });
    }

    onChangeFoodDescription(e) {
        this.setState({
            food_description: e.target.value
        });
    }

    onChangeFoodPrice(e) {
        this.setState({
            food_price: e.target.value
        });
    }
    */

    onSubmit(e) {
        e.preventDefault();

        console.log(`Form submitted:`);
        console.log(`Food Name: ${this.state.food_name}`);
        console.log(`Food Description: ${this.state.food_description}`);
        console.log(`Food Price: ${this.state.food_price}`);

        const newFood = {
            food_name: this.state.food_name,
            food_description: this.state.food_description,
            food_price: this.state.price
        };

        axios.post('http://localhost:4000/foods/add', newFood)
            .then(res => console.log(res.data));


        this.setState({
            food_name: '',
            food_description: '',
            food_price: ''
        })
    }

    render() {
        return (
            <div class="contact-clean">
                <form method="post">
                    <h1 class="text-uppercase text-center">Add Content</h1>
                        <label>Type</label>
                        <select class="form-control" name="Type" onChange={this.handleTypeChange}>
                            <optgroup label="Type of content">
                                <option value="canteen" selected="">Canteen</option>
                                <option value="stall">Stall</option>
                                <option value="food">Food</option>
                                <option value="news">News</option>
                                <option value="announcement">Announcement</option>
                            </optgroup>
                        </select>
                    <label>Name</label>
                    <input class="form-control" type="text" data-aos="fade" name="name" placeholder="Name" />
                    <label>Description</label>
                    <textarea class="form-control" name="message" placeholder="Message" rows="14"></textarea>
                    <label>Price</label>
                    <input class="form-control" type="text" data-aos="fade" name="name" placeholder="Name" />
                    <label>Image</label>
                    <input class="form-control" type="text" data-aos="fade" name="name" placeholder="Name" />
                    <label>Stall</label>
                    <input class="form-control" type="text" data-aos="fade" name="name" placeholder="Name" />
                    <div class="form-group">
                        <button class="btn btn-primary" type="submit">send </button>
                    </div>
                </form>
            </div>
        )
    }
}
