import React, { Component } from "react";
import { InfoConsumer } from "../context";
import styled from "styled-components";

class Details extends Component {
    render() {
        return (
            <div className="container">
                <ul class="nav nav-tabs">
                    {/* About Place Link */}
                    <li class="nav-item">
                        <a
                            href="#aboutPlace"
                            className="nav-link active"
                            role="tab"
                            data-toggle="tab"
                        >
                            About Place
                        </a>
                    </li>
                    {/* Review Link */}
                    <li class="nav-item">
                        <a
                            href="#reviews"
                            className="nav-link"
                            role="tab"
                            data-toggle="tab"
                        >
                            Reviews
                        </a>
                    </li>
                    {/* Map Link */}
                    <li class="nav-item">
                        <a
                            href="#map"
                            className="nav-link"
                            role="tab"
                            data-toggle="tab"
                        >
                            Map
                        </a>
                    </li>
                </ul>
                {/* Tab Pane */}
                <div className="tab-content mb-5">
                    {/* About Place Tab */}
                    <div
                        id="aboutPlace"
                        className="tab-pane in active text-center mt-5"
                        role="tabpanel"
                    >
                        <h2 className="mb-3">Title</h2>
                        <p>Description</p>
                        {/* img */}
                    </div>
                    {/* Reviews */}
                    <div className="tab-pane" id="reviews" role="tabpanel">
                        Reviews Content
                    </div>
                    {/* Map */}
                    <div className="tab-pane" id="map" role="tabpanel">
                        <iframe
                            src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d1123.3850145412064!2d37.600589158335865!3d55.72774864513666!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x46b54b0be3ac9d11%3A0x735806d25b3bdc55!2z0JzRg9C30LXQuSDRgdC-0LLRgNC10LzQtdC90L3QvtCz0L4g0LjRgdC60YPRgdGB0YLQstCwIMKr0JPQsNGA0LDQtsK7!5e0!3m2!1sru!2sru!4v1547763408711"
                            style={{
                                border: "0",
                                height: "28.125rem",
                                width: "100%",
                                frameborder: "0"
                            }}
                        />
                    </div>
                </div>
            </div>
        );
    }
}

export default Details;

const HeaderDetails = styled.header`
    background: linear-gradient(rgba(109, 109, 109), rgba(255, 255, 255));
`;
