import React, { Component } from "react";
import {
  Button,
  Input,
  Card,
  CardBody,
  CardImage,
  CardTitle,
  CardText
} from "mdbreact";

import blankImg from "./blank.gif";

import Modal from "react-bootstrap/Modal";

import "./style.css";
import "./flags.min.css";

import countriesList from "./countries.json";
import InfoConsumer from "../components/context"
import PopUp from "./components/PopUp";

export default class Search extends Component {
  state = {
    search: ""
  };

  editShow(show) {
    this.setState({ show: true });
  }

  renderFood = food => {
    const { search, link } = this.state;
    var code = food.code.toLowerCase();

    /*if( search !== "" && country.name.toLowerCase().indexOf( search.toLowerCase() ) === -1 ){
        return null
    }*/

    return (
      <div className="col-md-3" style={{ marginTop: "20px" }}>
        <Card>
          <CardBody>
            <p className="">
              <img
                src={food.image}
                alt={food.name}
              />
            </p>
            <CardTitle title={food.name}>
              {food.name.substring(0, 15)}
              {food.name.length > 15 && "..."}
            </CardTitle>
            <Button
              className="ml-2"
              variant="info"
              //onClick={() => this.editShow(country.link)}
              onClick={() => this.setState({ fassPenyetShow: true })}
            >
              More Info
            </Button>
          </CardBody>
        </Card>
      </div>
    );
  };

  constructor(props, context) {
    super(props, context);

    this.test = {
      fassPenyetShow: false,
      fassBakarShow: false
    };
  }

  onchange = e => {
    this.setState({ search: e.target.value });
  };

  render() {
    let fassPenyetClose = () => this.setState({ fassPenyetShow: false });
    let fassBakarClose = () => this.setState({ fassBakarShow: false });

    const { search } = this.state;
    const filteredCountries = countriesList.filter(country => {
      return country.name.toLowerCase().indexOf(search.toLowerCase()) !== -1;
    });

    return (
      <div className="flyout">
        <main style={{ marginTop: "4rem" }}>
          <div className="container">
            <PopUp foodImg=".././images/canteens/frontier/unclepenyet/frontierpenyet.jpg" show="true" foodClose={fassPenyetClose} />
        {/*
            <Modal
              size="lg"
              show={this.state.fassPenyetShow}
              onHide={fassPenyetClose}
              aria-labelledby="example-modal-sizes-title-lg"
            >
              <Modal.Header closeButton>
                <Modal.Title id="example-modal-sizes-title-lg">
                  More Info
                </Modal.Title>
              </Modal.Header>
              <Modal.Body>
                <h1>img1</h1>
              </Modal.Body>
            </Modal>

            <Modal
              size="lg"
              show={this.state.fassBakarShow}
              onHide={fassBakarClose}
              aria-labelledby="example-modal-sizes-title-lg"
            >
              <Modal.Header closeButton>
                <Modal.Title id="example-modal-sizes-title-lg">
                  More Info
                </Modal.Title>
              </Modal.Header>
              <Modal.Body>
                <h1>image2</h1>
              </Modal.Body>
            </Modal>
            */}

            <div className="row">
              <div className="col col-sm-10 offset-sm-1 text-center">
                <Input
                  label="Search Food"
                  icon="search"
                  onChange={this.onchange}
                />
              </div>
              <div className="col" />
            </div>
            <div className="row mb-5">
              {filteredCountries.map(food => {
                return this.renderFood(food);
              })}
            </div>
          </div>
        </main>
      </div>
    );
  }
}
