import React, { Component } from "react";
import InfoSnippet from "../InfoSnippet";
import Button from "react-bootstrap/Button";
import Location from "./Location";
import InfoConsumer from "../context.js";

export default class Info extends Component {
    constructor(props) {
        super(props);

        this.state = {
            item: this.props.item,
            faculty: this.props.faculty,
            show: false
        }
    }

    handleOnClose() {
        this.setState({
            show: false
        })
    }

    render() {
        return (
            <div>
                <Location
                    onClose={this.handleOnClose.bind(this)}
                    location={this.state.item.location}
                    show={this.state.show}
                />

                <h4 className="mt-5">
                    {this.state.faculty} Canteen
                        <Button
                            className="ml-2"
                            variant="info"
                            onClick={() => this.setState({ show: true })}
                        >
                            Location
                        </Button>
                </h4>
                <div className="row mt-3">
                    <InfoConsumer>
                        {value => {
                            return value.canteens
                                .filter(canteen => canteen.faculty === this.state.faculty)
                                .then(item =>
                                    <InfoSnippet key={this.props.item._id} item={this.props.item} />);
                        }}
                    </InfoConsumer>
                </div>
            </div>
        );
    }
}
