import React, { Component } from "react";
import axios from 'axios';
/*
import {
    placeInfo,
    placeInfo2,
    scienceCanteen,
    fassCanteen,
    detailInfo,
    scienceStall,
    fassStall
} from "../data";
*/

const InfoContext = React.createContext();

class InfoProvider extends Component {
    state = {
        info: '',//placeInfo,
        info2: '',//placeInfo2,
        detailInfo: '',//detailInfo,
        scienceCanteen: '',//scienceCanteen,
        scienceStall: '',//scienceStall,
        fassStall: '',//fassStall,
        fassCanteen: ''//fassCanteen
    };

    componentDidMount() {
        axios.get('http://localhost:4000/foods/')
            .then(res => {
                this.setState({ 
                    info: res.data.info,
                    info2: res.data.info2,
                    detailInfo: res.data.detailInfo,
                    scienceCanteen: res.data.scienceCanteen,
                    scienceStall: res.data.scienceStall,
                    fassStall: res.data.fassStall,
                    fassCanteen: res.data.fassCanteen
                });
            })
            .catch(function (err) {
                console.log(err);
            })
        axios.get('http://localhost:4000/info/')
            .then(res => {
                this.setState({ 
                    info: res.data.info
                });
            })
            .catch(function (err) {
                console.log(err);
            });
        axios.get('http://localhost:4000/info2/')
            .then(res => {
                this.setState({ 
                    info2: res.data.info2
                });
            })
            .catch(function (err) {
                console.log(err);
            });
        axios.get('http://localhost:4000/detailInfo/')
            .then(res => {
                this.setState({ 
                    detailInfo: res.data.detailInfo
                });
            })
            .catch(function (err) {
                console.log(err);
            });
        axios.get('http://localhost:4000/scienceCanteen/')
            .then(res => {
                this.setState({ 
                    scienceCanteen: res.data.scienceCanteen
                });
            })
            .catch(function (err) {
                console.log(err);
            });
        axios.get('http://localhost:4000/scienceStall/')
            .then(res => {
                this.setState({ 
                    scienceStall: res.data.scienceStall
                });
            })
            .catch(function (err) {
                console.log(err);
            })
        axios.get('http://localhost:4000/fassCanteen/')
            .then(res => {
                this.setState({ 
                    fassCanteen: res.data.fassCanteen
                });
            })
            .catch(function (err) {
                console.log(err);
            })
    }

    getItem = id => {
        const item = this.state.info.find(item => item.id === id);
        return item;
    };

    handleDetail = id => {
        const item = this.getItem(id);
        this.setState(() => {
            return {
                detailInfo: item
            };
        });
    };

    getItem2 = id => {
        const item2 = this.state.info2.find(item2 => item2.id === id);
        return item2;
    };

    handleDetail2 = id => {
        const item2 = this.getItem2(id);
        this.setState(() => {
            return {
                detailInfo: item2
            };
        });
    };

    render() {
        return (
            <InfoContext.Provider
                value={{
                    info: this.state.info,
                    info2: this.state.info2,
                    price: this.state.price,
                    maps: this.state.maps,
                    scienceCanteen: this.state.scienceCanteen,
                    scienceStall: this.state.scienceStall,
                    fassStall: this.state.fassStall,
                    fassCanteen: this.state.fassCanteen,
                    headerTitle: this.state.headerTitle,
                    headerSubTitle: this.state.headerSubTitle,
                    headerText: this.state.headerText,
                    detailInfo: this.state.detailInfo,
                    name: this.state.name,
                    avatar: this.state.avatar,
                    comment: this.state.comment,
                    handleDetail: this.handleDetail,
                    handleDetail2: this.handleDetail2,
                    stall: this.state.stall
                }}
            >
                {this.props.children}
            </InfoContext.Provider>
        );
    }
}

const InfoConsumer = InfoContext.Consumer;

export { InfoProvider, InfoConsumer };
