import React, { Component } from "react";
import { InfoConsumer } from "../context";
import Info from "../Info";
import Info2 from "../Info2";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";

class Canteens extends Component {
    constructor(props, context) {
        super(props, context);

        this.state = {
            smShow: false,
            lgShow: false
        };
    }

    render() {
        let smClose = () => this.setState({ smShow: false });
        let lgClose = () => this.setState({ lgShow: false });
        return (
            <div className="container">
                <Modal
                    size="lg"
                    show={this.state.lgShow}
                    onHide={lgClose}
                    aria-labelledby="example-modal-sizes-title-lg"
                >
                    <Modal.Header closeButton>
                        <Modal.Title id="example-modal-sizes-title-lg">
                            Large Modal
                        </Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        {" "}
                        <iframe
                            src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d5691.965642731867!2d103.77773491402935!3d1.2958058252296165!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x31da1a50d2a84f95%3A0x10368845ce4e2bb0!2sNUS+Science+Canteen+(Frontier)!5e0!3m2!1sen!2ssg!4v1561537467347!5m2!1sen!2ssg"
                            width="770"
                            height="450"
                        />
                    </Modal.Body>
                </Modal>
                <h4 className="mt-5">
                    Science Canteen
                    <Button
                        className="ml-2"
                        variant="info"
                        onClick={() => this.setState({ lgShow: true })}
                    >
                        Location
                    </Button>
                </h4>
                <div className="row mt-3">
                    <InfoConsumer>
                        {value => {
                            return value.scienceCanteen.map(item => {
                                return <Info key={item.id} item={item} />;
                            });
                        }}
                    </InfoConsumer>
                </div>

                <h4 className="mt-5">
                    Fass Canteen
                    <Button className="ml-2" variant="info">
                        Location
                    </Button>
                </h4>
                <div className="row mt-3">
                    <InfoConsumer>
                        {value => {
                            return value.fassCanteen.map(item2 => {
                                return <Info2 key={item2.id} item2={item2} />;
                            });
                        }}
                    </InfoConsumer>
                </div>
            </div>
        );
    }
}

export default Canteens;
