import React, { Component } from "react";
import CommentBox from "../CommentBox";
import Comments from "../Comments";
import { InfoConsumer } from "../context";
import styled from "styled-components";

class Details extends Component {
    constructor(props) {
        super(props);
        this.handleAddComment = this.handleAddComment.bind(this);
        this.state = {
            comments: []
        };
    }

    handleAddComment(comment) {
        this.setState(prevState => {
            return {
                comments: prevState.comments.concat(comment)
            };
        });
    }

    render() {
        return (
            <div className="container">
                <ul class="nav nav-tabs mt-5">
                    {/* About Place Link */}
                    <li class="nav-item">
                        <a
                            href="#aboutFood"
                            className="nav-link active"
                            role="tab"
                            data-toggle="tab"
                        >
                            About Food
                        </a>
                    </li>
                    {/* Review Link */}
                    <li class="nav-item">
                        <a
                            href="#reviews"
                            className="nav-link"
                            role="tab"
                            data-toggle="tab"
                        >
                            Reviews
                        </a>
                    </li>
                    {/* Map Link */}
                    <li class="nav-item">
                        <a
                            href="#map"
                            className="nav-link"
                            role="tab"
                            data-toggle="tab"
                        >
                            Map
                        </a>
                    </li>
                </ul>
                {/* Tab Pane */}
                <div className="tab-content mb-5">
                    {/* About Place Tab */}
                    <div
                        id="aboutFood"
                        className="tab-pane in active text-center mt-5"
                        role="tabpanel"
                    >
                        <h2 className="mb-3">Title</h2>
                        <p>
                            Lorem ipsum dolor, sit amet consectetur adipisicing
                            elit. Odit laboriosam cum recusandae quas? Natus,
                            alias architecto error, sunt quae non placeat a eius
                            minus maiores libero laborum perferendis, nobis qui?
                        </p>
                        <p>Price: Location:</p>
                        <img
                            className="d-block w-55 mx-auto"
                            src=".././images/800x400.png"
                            alt="Chicken Rice"
                        />
                    </div>
                    {/* Reviews */}
                    <div className="tab-pane" id="reviews" role="tabpanel">
                        <div className="columns mt-5">
                            <div className="column is-half is-offset-one-quarter">
                                <CommentBox
                                    handleAddComment={this.handleAddComment}
                                />
                                <Comments comments={this.state.comments} />
                            </div>
                        </div>
                    </div>
                    {/* Map */}
                    <div className="tab-pane" id="map" role="tabpanel">
                        <iframe
                            src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d1123.3850145412064!2d37.600589158335865!3d55.72774864513666!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x46b54b0be3ac9d11%3A0x735806d25b3bdc55!2z0JzRg9C30LXQuSDRgdC-0LLRgNC10LzQtdC90L3QvtCz0L4g0LjRgdC60YPRgdGB0YLQstCwIMKr0JPQsNGA0LDQtsK7!5e0!3m2!1sru!2sru!4v1547763408711"
                            style={{
                                border: "0",
                                height: "28.125rem",
                                width: "100%",
                                frameborder: "0"
                            }}
                        />
                    </div>
                </div>
            </div>
        );
    }
}

export default Details;
