import React, { Component } from 'react';
import axios from 'axios';
import AddCanteen from './AddContent/AddCanteen';

export default class AddContent extends Component {

    constructor(props) {
        super(props);

        //this.onChangeFoodName = this.onChangeFoodName.bind(this);
        //this.onChangeFoodDescription = this.onChangeFoodDescription.bind(this);
        //this.onChangeFoodPrice = this.onChangeFoodPrice.bind(this);
        this.handleTypeChange = this.handleTypeChange.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            type: 'canteen',
            food_name: '',
            food_description: '',
            food_price: '',
            newsTitle: '',
            newsText: ''
        }
    }

    handleTypeChange(e) {
        this.setState({type: e.target.value});
    }

    handleChange = ({ target }) => {
        this.setState({ [target.name]: target.value});
    };

    onChangeName(e) {
        this.setState({
            name: e.target.value
        });
    }

    onChangeDescription(e) {
        this.setState({
            description: e.target.value
        });
    }

    onChangePrice(e) {
        this.setState({
            price: e.target.value
        });
    }

    onSubmit(e) {
        e.preventDefault();

        console.log(`Form submitted:`);
        console.log(`Food Name: ${this.state.food_name}`);
        console.log(`Food Description: ${this.state.food_description}`);
        console.log(`Food Price: ${this.state.food_price}`);

        const newFood = {
            food_name: this.state.food_name,
            food_description: this.state.food_description,
            food_price: this.state.price
        };

        axios.post('http://localhost:4000/foods/add', newFood)
            .then(res => console.log(res.data));


        this.setState({
            food_name: '',
            food_description: '',
            food_price: ''
        })
    }

    render() {
        return (
            <div class="contact-clean">
                <form method="post" style={{margin: ' 10em 25em'}}>
                    <h1 class="text-uppercase text-center">Add Content</h1>
                        <label>Type</label>
                        <select 
                            class="form-control" 
                            name="Type" 
                            value={this.state.value} 
                            onChange={this.handleTypeChange}
                        >
                            <optgroup label="Type of content">
                                <option value="canteen">Canteen</option>
                                <option value="stall">Stall</option>
                                <option value="food">Food</option>
                                <option value="news">News</option>
                                <option value="announcement">Announcement</option>
                            </optgroup>
                        </select>
                    <AddCanteen handleChange={this.handleChange} />
            {/*
                    <Input name="name" value={this.state.name}/>
                    <label name="description">Description</label>
                        <textarea 
                            class="form-control" 
                            name="description" 
                            rows="14"
                        >
                        </textarea>
                    <Input name="price" value={this.state.price}/>
                    <Input name="image" value={this.state.image}/>
                    <Input name="stall" value={this.state.stall}/>
             */} 
                    <div class="form-group" style={{margin: '9px 0px'}}>
                        <button class="btn btn-primary" type="submit">
                            send 
                        </button>
                    </div>
                </form>
            </div>
        )
    }
}
